definition.script:
pipeline {
    agent any

    stages {
        stage('Install Docker CLI') {
            steps {
                sh 'curl -fsSL https://get.docker.com -o get-docker.sh'
                sh 'sh get-docker.sh'
                sh 'usermod -aG docker jenkins'
                // Перезапуск Jenkins, чтобы применить изменения в группах пользователей
                sh 'docker restart jenkins'
            }
        }
        stage('Run Autotests') {
            steps {
                script {
                    docker.image('ianwalter/puppeteer:latest').inside {
                       sh './init.sh'
                    }
                }
            }
        }
    }
}
